import { createSlice } from "@reduxjs/toolkit";

const initialState = [];

const checkboxSlice = createSlice({
  name: "checkbox",
  initialState,
  reducers: {
    toggleCheckbox(state, action) {
      const { id } = action.payload;
      const item = state.todos.find((item) => item.id === id);
      if (item) {
        item.checked = !item.checked;
        if (item.checked) {
          const index = state.todos.indexOf(item);
          state.todos.push(state.todos.splice(index, 1)[0]);
        }
      }
    },
  },
});

export const { toggleCheckbox } = checkboxSlice.actions;
export const checkboxReducer = checkboxSlice.reducer;
