import { createSlice } from "@reduxjs/toolkit";

const initialState = [
  { key: 1, id: Date.now(), todo: "Check email", completed: false },
  { key: 2, id: Date.now(), todo: "Walk the dog", completed: false },
  { key: 3, id: Date.now(), todo: "Go to gym", completed: false },
];

const ToDoSlice = createSlice({
  name: "todos",
  initialState,
  reducers: {
    addTodos: (state, action) => {
      return [...state, action.payload];
    },
    removeTodos: (state, action) => {
      return state.filter((todo) => todo.id !== action.payload);
    },
    completeTodos: (state, action) => {
      return state.map((todo) => {
        if (todo.id === action.payload) {
          return {
            ...todo,
            completed: true,
          };
        }
        console.log(todo);
        return todo;
      });
    },
  },
});

export const { addTodos, removeTodos, updateTodos, completeTodos, toggleTodo } =
  ToDoSlice.actions;
export const toDoRerducer = ToDoSlice.reducer;
