import { createSlice } from "@reduxjs/toolkit";

const initialState = {
  todos: [
    { id: 1, text: "Item 1", checked: false },
    { id: 2, text: "Item 2", checked: false },
    { id: 3, text: "Item 3", checked: false },
    { id: 4, text: "Item 4", checked: false },
    { id: 5, text: "Item 5", checked: false },
  ],
};

const checkboxSlice = createSlice({
  name: "checkbox",
  initialState,
  reducers: {
    toggleCheckbox(state, action) {
      const { id } = action.payload;
      const item = state.todos.find((item) => item.id === id);
      if (item) {
        item.checked = !item.checked;
        if (item.checked) {
          const index = state.todos.indexOf(item);
          state.todos.push(state.todos.splice(index, 1)[0]);
        }
      }
    },
  },
});

export const { toggleCheckbox } = checkboxSlice.actions;
export const checkboxRerducer = checkboxSlice.reducer;
