import React, { useRef } from "react";
import { IoCheckmarkDoneSharp, IoClose } from "react-icons/io5";
import { addTodos, completeTodos, removeTodos } from "../../redux/ToDoReducer";
import { connect } from "react-redux";

const mapStateToProps = (state) => {
    return {
        todos: state,
    };
};

const mapDispatchToProps = (dispatch) => {
    return {
        addTodo: (obj) => dispatch(addTodos(obj)),
        removeTodo: (id) => dispatch(removeTodos(id)),
        completeTodo: (id) => dispatch(completeTodos(id)),
    };
};

const SingleTodo = (props) => {

    const { removeTodo, completeTodo } = props;

    const items = useSelector(state => state.checkbox.items);
    const dispatch = useDispatch();

    const handleCheckboxChange = (id) => {
        dispatch(toggleCheckbox({ id }));
    };

    return (
        <ul className="w-1/3 mt-10 bg-cyan-600 py-6 rounded">
            {props.todos.length > 0 ?
                props.todos.map((todo) => {
                    return (<li
                        key={todo.id}
                        className=" text-slate-200 border-b border-cyan-700 px-8 py-3 flex justify-between items-center"
                    >
                        <div>
                            <input
                                id={todo.id}
                                type="checkbox"
                                style={{ accentColor: "#1E6091" }}
                                className="w-6 h-4 mr-4 checked:appearance-none"
                                onChange={() => {
                                    completeTodo(todo.id)
                                }}
                            />
                            {todo.completed
                                ? <del className="completed">{todo.todo}</del>
                                :
                                <label className="" htmlFor={todo.id}>{todo.todo}</label>
                            }
                        </div>
                        <button
                            style={{ color: "red" }}
                            onClick={() => removeTodo(todo.id)} >
                            <IoClose />
                        </button>
                    </li>)
                })
                : <p className="text-center text-white text-3xl">ðŸ¥³ You made it, Bravo!</p>}
        </ul>
    );
};

export default connect(mapStateToProps, mapDispatchToProps)(SingleTodo);
