import React, { useRef } from "react";
import { AiFillEdit } from "react-icons/ai";
import { IoCheckmarkDoneSharp, IoClose } from "react-icons/io5";
import { addTodos, completeTodos, removeTodos, updateTodos } from "../../redux/reducer";
import { connect } from "react-redux";

const mapStateToProps = (state) => {
    return {
        todos: state,
    };
};

const mapDispatchToProps = (dispatch) => {
    return {
        addTodo: (obj) => dispatch(addTodos(obj)),
        removeTodo: (id) => dispatch(removeTodos(id)),
        updateTodo: (obj) => dispatch(updateTodos(obj)),
        completeTodo: (id) => dispatch(completeTodos(id)),
    };
};

const SingleTodo = (props) => {

    const { updateTodo, removeTodo, completeTodo } = props;

    const inputRef = useRef(true);

    const changeFocus = () => {
        inputRef.current.disabled = false;
        inputRef.current.focus();
    };
    const update = (id, value, e) => {
        if (e.which === 13) {
            updateTodo({ id, todo: value });
            inputRef.current.disabled = true;
        }
    };
    console.log(inputRef.current.disabled, "inputref")

    return (
        <ul className="w-1/3 mt-10 bg-cyan-600 py-6 rounded">
            {props.todos.length > 0 &&
                props.todos.map((todo) => {
                    return (<li
                        key={todo.id}
                        className=" text-slate-200 border-b border-cyan-700 px-8 py-3 flex justify-between"
                    >
                        {todo.completed
                            ? <del className="completed">{todo.todo}</del>
                            : <>
                                <input
                                    type="checkbox"
                                    style={{ accentColor: "#1E6091" }}
                                    className="w-5"
                                    onChange={() => {
                                        completeTodo(todo.id)
                                    }}
                                />
                                <input
                                    ref={inputRef}
                                    disabled={inputRef.current.disabled}
                                    defaultValue={todo.todo}
                                    onKeyPress={(e) => update(todo.id, inputRef.current.value, e)}
                                    className="bg-transparent"
                                />
                            </>}
                        <div className="space-x-3">
                            {todo.completed === false && (
                                <>
                                    {inputRef.current.disabled ? <button onClick={() => changeFocus()}>
                                        <AiFillEdit />
                                    </button>
                                        : <button onClick={() => changeFocus()}>
                                            <IoCheckmarkDoneSharp />
                                        </button>}
                                    <button
                                        style={{ color: "green" }}
                                        onClick={() => completeTodo(todo.id)}
                                    >
                                        <IoCheckmarkDoneSharp />
                                    </button>
                                </>
                            )}
                            <button
                                style={{ color: "red" }}
                                onClick={() => removeTodo(todo.id)} >
                                <IoClose />
                            </button>
                        </div>
                    </li>)
                })}
        </ul>
    );
};

export default connect(mapStateToProps, mapDispatchToProps)(SingleTodo);
