import { createSlice, current } from "@reduxjs/toolkit";

const initialState = [
  { key: 1, id: 1, todo: "Check email", completed: false },
  { key: 2, id: 2, todo: "Walk the dog", completed: false },
  { key: 3, id: 3, todo: "Go to gym", completed: false },
];

const ToDoSlice = createSlice({
  name: "todos",
  initialState,
  reducers: {
    addTodos: (state, action) => {
      return [...state, action.payload];
    },
    removeTodos: (state, action) => {
      return state.filter((todo) => todo.id !== action.payload);
    },
    completeTodos: (state, action) => {
      let to = 0;
      let from = 0;
      return state.map((todo, index) => {
        if (todo.id === action.payload) {
          console.log(current(state), "before");
          console.log(current(todo), "todo");
          return {
            ...todo,
            completed: true,
          };
        }
        from = index;
        return state.splice(to, 0, state.splice(from, 1)[0]);
        // console.log(current(state), "after");
        // return todo;
      });
    },
  },
});

export const { addTodos, removeTodos, updateTodos, completeTodos, toggleTodo } =
  ToDoSlice.actions;
export const toDoRerducer = ToDoSlice.reducer;
