import React, { useState } from "react";
import { connect } from "react-redux";
import { addTodos } from "../../redux/ToDoReducer";
import { keyboard } from "@testing-library/user-event/dist/keyboard";

const mapStateToProps = (state) => {
    return {
        todos: state.todos,
    };
};

const mapDispatchToProps = (dispatch) => {
    return {
        addTodo: (obj) => dispatch(addTodos(obj)),
    };
};

const Todos = (props) => {
    const [todo, setTodo] = useState("");


    const addNewToDo = (e) => {
        if (todo === "") {
            alert("Enter the title, please");
        } else if (!todo.match(/^[a-zA-Z]+$/)) {
            alert("You can only enter words");
            e.reset()
        } else {
            props.addTodo({
                id: Date.now(),
                todo: todo,
                completed: false,
            });
            setTodo("");
        }
    };

    const handleChange = (e) => {
        setTodo(e.target.value);
    }

    return (
        <div className="w-1/3 flex">
            <input type="text"
                className="block w-full p-3 ps-10  text-gray-100 border border-cyan-700 rounded-s-lg bg-cyan-600 focus:ring-cyan-500 focus:border-cyan-500 "
                onChange={(e) => handleChange(e)}
                onKeyDown={(e) => {
                    if (e.key === "Enter")
                        addNewToDo();
                }}
                value={todo}
                placeholder="Add a new todo..."
                required
            />
            <button
                onClick={(e) => addNewToDo(e)}
                className="text-white bg-cyan-700 hover:bg-cyan-800 focus:ring-4 focus:outline-none focus:ring-cyan-600 font-medium rounded-e-lg px-6 py-2">
                Add
            </button>
        </div>
    )
}

export default connect(mapStateToProps, mapDispatchToProps)(Todos);
