import React, { useRef } from "react";
import { IoCheckmarkDoneSharp, IoClose } from "react-icons/io5";
import { addTodos, completeTodos, removeTodos } from "../../redux/reducer";
import { connect } from "react-redux";

const mapStateToProps = (state) => {
    return {
        todos: state,
    };
};

const mapDispatchToProps = (dispatch) => {
    return {
        addTodo: (obj) => dispatch(addTodos(obj)),
        removeTodo: (id) => dispatch(removeTodos(id)),
        completeTodo: (id) => dispatch(completeTodos(id)),
    };
};

const SingleTodo = (props) => {

    const { removeTodo, completeTodo } = props;

    return (
        <ul className="w-1/3 mt-10 bg-cyan-600 py-6 rounded">
            {props.todos.length > 0 ?
                props.todos.map((todo) => {
                    return (<li
                        key={todo.id}
                        className=" text-slate-200 border-b border-cyan-700 px-8 py-3 flex justify-between"
                    >
                        {todo.completed
                            ? <del className="completed">{todo.todo}</del>
                            : <>
                                <input
                                    type="checkbox"
                                    style={{ accentColor: "#1E6091" }}
                                    className="w-5"
                                    onChange={() => {
                                        completeTodo(todo.id)
                                    }}
                                />
                                <label htmlFor="">{todo.todo}</label>
                            </>}
                        <button
                            style={{ color: "red" }}
                            onClick={() => removeTodo(todo.id)} >
                            <IoClose />
                        </button>
                    </li>)
                })
                : <p className="text-center text-white text-3xl">ðŸ¥³ You made it, Bravo!</p>}
        </ul>
    );
};

export default connect(mapStateToProps, mapDispatchToProps)(SingleTodo);
